import React from 'react';
import { Bar, Line } from 'react-chartjs-2';

var Width = document.documentElement.clientWidth;
export default class EventStatistics extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            calorie: [],
            step: [],
            distance: [],
            duration: [],
            activity: []
        }
    }

    componentDidMount() {

    }

    generateData(name) {
        const colors = ['#B0C4DE', '#ADD8E6', '#B0E0E6', '#AFEEEE', '#B3EE3A', '#9ACD32', "#84bf96", "#45b97c", "#367459", "#abc88b"];
        const backgroundColor = colors.slice(0, this.state.activity.size());
        const data = {
            labels: this.state.activity,
            datasets: [
                {
                    label: `${name}`,
                    backgroundColor,
                    borderColor: '#458B00',
                    borderWidth: 1,
                    hoverBackgroundColor: '#E0FFFF',
                    hoverBorderColor: '#4682B4',
                    data: this.state[name],
                    fill: 'red'
                },
            ],
        };
        return data;
    }

    populateData() {
        const types = ["calorie", "step", "distance", "duration"];
        return types.map(type => this.generateData(type));
    }

    render() {
        if (this.state.activity === []) {
            return <div>Loading ...</div>
        }
        const dataSet = this.populateData();
        const chart = dataSet.map(data => <Line data={data} width={Width} height="1000" />);
        return (
            <div style={{ height: '50%', width: '50%' }}>
                <Bar data={data} width={Width} height="1000" />
                <Line data={data} width={Width} height="1000" />
            </div>
        );
    }
}
