import React from 'react';
import { Bar, Line } from 'react-chartjs-2';

var Width = document.documentElement.clientWidth;
export default class EventStatistics extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            calorie: [],
            step: [],
            distance: [],
            duration: [],
            activity: []
        }
    }

    componentDidMount() {

    }

    populateData() {
        this.state.array.forEach(element => {
            console.log(element);
        });
    }

    render() {
        this.populateData();
        const data = {
            labels: ['walking', 'transport', 'running', 'cycling', 'kayaking', 'airplane'],
            datasets: [
                {
                    label: 'Distance taken for each type of Activities',
                    backgroundColor: ['#B0C4DE', '#ADD8E6', '#B0E0E6', '#AFEEEE', '#B3EE3A', '#9ACD32'],
                    borderColor: '#458B00',
                    borderWidth: 1,
                    hoverBackgroundColor: '#E0FFFF',
                    hoverBorderColor: '#4682B4',
                    data: [65, 59, 80, 81, 56, 55],
                    fill: 'red'
                },
            ],
        };
        return (
            <div style={{ height: '50%', width: '50%' }}>
                <Bar data={data} width={Width} height="1000" />
                <Line data={data} width={Width} height="1000" />
            </div>
        );
    }
}
